let jsonData=[],birthDates=[],personObj=[],days=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];const yearField=document.getElementById("year-field"),updateBtn=document.getElementById("update-btn"),listItems=document.querySelectorAll(".day"),birthDayDiv=document.querySelectorAll(".birthDays"),birthDaysTextArea=document.getElementById("birth-dates-textarea");birthDayDiv.forEach((e=>e.innerHTML=""));const getData=()=>{fetch("assets/json/got-characters-birthday.json").then((e=>e.json())).then((e=>e.filter((e=>jsonData.push(e)))))};function createPersonObj(){jsonData.forEach((e=>{let t=e.name.match(/\b(\w)/g).join(""),a=e.birthday.slice(0,6)+userInput,n=new Date(a),r={name:t,birthDay:days[n.getDay()].substring(0,3)};personObj.push(r)})),displayBirthdays()}fetch("assets/json/got-characters-birthday.json").then((e=>e.json())).then((e=>e.filter((e=>jsonData.push(e)))));const displayBirthdays=()=>{personObj.forEach((e=>{let t=Math.floor(16777215*Math.random()).toString(16),a=e.birthDay.toLowerCase();listItems.forEach((function(n){if(n.id.toLocaleLowerCase()===a){let a=document.createElement("span"),r=n.querySelector(".birthDays");a.style.backgroundColor=`#${t}`,a.innerHTML=e.name,r.appendChild(a)}}))}))};updateBtn.addEventListener("click",(e=>{e.preventDefault(),userInput=yearField.value,createPersonObj()}));